import React from 'react'
import { useEffect,useState } from 'react'
import axios from "axios"
import {Button,CardFooter,Card,Text,Image,Stack,CardBody,Heading}from "@chakra-ui/react"

export const History = () => {
    const[income,setIncome]=useState([])
    const[expense,setExpense]=useState([])
    const[total,setTotal]=useState([])

    const getIncome=async()=>{
        const res= await axios.get('http://localhost:8000/userIncome')
          console.log(res.data)
          setIncome(res.data)
          const array3 = income.concat(expense);
          console.log(array3)
          setTotal(array3)
    } 
    const getExpenses=async()=>{
        const res= await axios.get('http://localhost:8000/userExpense')
          console.log(res.data)
          setExpense(res.data)
         
    } 

    useEffect(()=>{
     
        getIncome()
    },[])
    useEffect(()=>{
     
        getExpenses()
    },[])
    const join=()=>{
        const array3 = income.concat(expense);
        console.log(array3)
        setTotal(array3)
    }
          const delfun=async(idx,type)=>{
               if(type=="income"){
                let res=axios.delete(`http://localhost:8000/userIncome/:{id}`)
                console.log(res)
               }else if(type=="expenses"){
                let res=axios.delete(`http://localhost:8000/userExpense/:{id}`)
                console.log(res)
               }
          }

  return (
    <div>
<Button onClick={join}>ShowData</Button>

   { total.length>0 && total.map(function(ele,idx){
        return <div>
            <Card
  direction={{ base: 'column', sm: 'row' }}
  overflow='hidden'
  variant='outline'
  w="80%"
>
  <Image
    objectFit='cover'
   w="50%"
    src='https://images.unsplash.com/photo-1667489022797-ab608913feeb?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxlZGl0b3JpYWwtZmVlZHw5fHx8ZW58MHx8fHw%3D&auto=format&fit=crop&w=800&q=60'
    alt='Caffe Latte'
  />

  <Stack>
    <CardBody>
      <Heading size='md'>{ele.type}</Heading>

      <Text py='2'>
       <h1> {ele.income}</h1>
       <h1> "expenses" :{ele.expenses}</h1>
        &nbsp;
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        {ele.amount}
        &nbsp;
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        {ele.date}
      </Text>
    </CardBody>

    <CardFooter>
      <Button variant='solid' colorScheme='blue' onClick={(e)=>{delfun(ele.id,ele.type)}}>
       delete
      </Button>
    </CardFooter>
  </Stack>
</Card>
</div>
    })
}

    </div>
  )
}
