import React from 'react'
import { useEffect,useState } from 'react'
import axios from "axios"
import {Button,CardFooter,Card,Text,Image,Stack,CardBod}from "@chakra-ui/react"

export const History = () => {
    const[income,setIncome]=useState([])
    const[expense,setExpense]=useState([])
    const[total,setTotal]=useState([])

    const getIncome=async()=>{
        const res= await axios.get('http://localhost:8000/userIncome')
          console.log(res.data)
          setIncome(res.data)
    } 
    const getExpenses=async()=>{
        const res= await axios.get('http://localhost:8000/userExpense')
          console.log(res.data)
          setExpense(res.data)
          const array3 = income.concat(expense);
          console.log(array3)
          setTotal(array3)
    } 

    useEffect(()=>{
     
        getIncome()
    },[])
    useEffect(()=>{
     
        getExpenses()
    },[])
  return (
    <div>
{
    total.length>0 && total.map(function(ele,idx){
        return <div>
            <Card
  direction={{ base: 'column', sm: 'row' }}
  overflow='hidden'
  variant='outline'
>
  <Image
    objectFit='cover'
    maxW={{ base: '100%', sm: '200px' }}
    src='https://images.unsplash.com/photo-1667489022797-ab608913feeb?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxlZGl0b3JpYWwtZmVlZHw5fHx8ZW58MHx8fHw%3D&auto=format&fit=crop&w=800&q=60'
    alt='Caffe Latte'
  />

  <Stack>
    <CardBody>
      <Heading size='md'>The perfect latte</Heading>

      <Text py='2'>
        Caff√® latte is a coffee beverage of Italian origin made with espresso
        and steamed milk.
      </Text>
    </CardBody>

    <CardFooter>
      <Button variant='solid' colorScheme='blue'>
        Buy Latte
      </Button>
    </CardFooter>
  </Stack>
</Card>
        </div>
    })
}

    </div>
  )
}
