import React from 'react'
import { useState } from 'react'
import axios from "axios"

export const Tracker = () => {
    const[type,setType]=useState("")
    const[income,setIncome]=useState("")
    const[expenses,setExpenses]=useState("")
    const[amount,setAmount]=useState(0)
    const[date,setDate]=useState("")

    const sendData=async(e)=>{
        e.preventDefault()
        if(type=="income"){
            let data={
                type,
                income,
                amount,
                date
            }

            
            try {
            const res = await axios.post('http://localhost:8000/userIncome', data)
            console.log(res.data)
          } catch (e) {
            // alert(e)
            console.log(e)
          }

        console.log(data)
    }else if(type=="expenses"){
        let secdata={
            type,
            expenses,
            amount,
            date
        }
        try {
            

            let response = await axios.post('http://localhost:8000/userExpense', secdata)
            console.log(response.secdata)
          } catch (e) {
            // alert(e)
            console.log(e)
          }

        console.log(secdata)
    }
}
  return (
    <div>
        <form onSubmit={(e)=>{sendData(e)}} >

<select onChange={(e)=>setType(e.target.value)}>
<option value="">Type</option>
<option value="income">Income</option>
<option value="expenses">Expenses</option>

</select>

{type=="income"&&
<select  id="" onChange={(e)=>setIncome(e.target.value)}>
<option value="">Category</option>
<option value="salary">Salary</option>
<option value="gifts">Gifts</option>
<option value="refunds">Refunds</option>
<option value="others">Others</option>
</select>
}
{type=="expenses"&&
<select  id="" onChange={(e)=>setExpenses(e.target.value)}>
<option value="">Category</option>
<option value="food">Food and Drinks</option>
<option value="shopping">Shopping</option>
<option value="housing">Housing</option>
<option value="bills">Bills</option>
<option value="vehicle">Vehicle and Transport</option>
<option value="lifestyle">Lifestyle</option>
</select>
}
<input type="number" placeholder='Amount' onChange={(e)=>setAmount(e.target.value)}/>
<input type="date" placeholder='Date' onChange={(e)=>setDate(e.target.value)}/>

<input type="submit" placeholder='Submit'/>


        </form>
    </div>
  )
}
